import React, { Component } from 'react';
import {Image} from 'cloudinary-react'
import Web3 from 'web3';
import SaveChildren from '../abis/SaveChildren.json'
import '../components/App.css';
import Discover from './Discover';
import App from '../components/App';
import ReactDOM from "react-dom";
import ProgressBar from 'react-bootstrap/ProgressBar';
import emailjs from 'emailjs-com';


class Home extends Component{

    constructor(props){
        super(props)
        this.state = {
            contract: null,
            account: '',
            campaign: '',
            amount: '',
            email: '',
        }
    }

    updateEmail(evt) {
        this.setState({
            email: evt.target.value
        })

    }

    async componentWillMount() {
        await this.loadWeb3()
        await this.loadBlockchainData()
    }


    async loadWeb3() {
        if (window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        }
        else if (window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
        }
        else {
            window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
        }
    }


    async loadBlockchainData() {
        const web3 = window.web3
        // Load account
        const accounts = await web3.eth.getAccounts()
        this.setState({account: accounts[0]})

        const networkId = await web3.eth.net.getId()
        const networkData = SaveChildren.networks[networkId]
        if(networkData) {
            const abi = SaveChildren.abi
            const address = networkData.address
            const contract = new web3.eth.Contract(abi, address)

            this.setState({ contract: contract})

            console.log(this.state.date)

        } else {
            window.alert('Smart contract not deployed to detected network')
        }

    }

    moveToDiscover = () => {
        ReactDOM.render(<Discover />, document.getElementById('root'))
    }

    upadateAmount(evt) {
        this.setState({
            amount: evt.target.value
        });
    }

    donate = () => {
        this.state.contract.methods.donate(parseInt(this.state.campaign.id), this.state.email)
            .send({from: this.state.account, value: parseFloat(this.state.amount)*(10**18)})


        var templateParams = {
            user_email: this.state.campaign.email,
        };
        emailjs.send('service_7zotz9y', 'template_2uirx3l', templateParams, 'user_4u7WjbA2GZUJYYM6i8nrV')
            .then(function(response) {
                console.log('SUCCESS!', response.status, response.text);
            }, function(error) {
                console.log('FAILED...', error);
            });
    }

    sendEmail(e) {
        e.preventDefault();

        emailjs.sendForm('service_7zotz9y', 'template_2uirx3l', e.target, 'user_4u7WjbA2GZUJYYM6i8nrV')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
    }

    moveToDiscover = () => {
        ReactDOM.render(<Discover />, document.getElementById('root'))
    }

    moveToCreate = () => {
        ReactDOM.render(<App />, document.getElementById('root'))
    }

    render() {
        return (
            <div>
                <meta charSet="utf-8" />
                <meta name="viewport" content="width=device-width, initial-scale=1, shrink-to-fit=no" />
                <meta name="description" content />
                <meta name="author" content />
                <title>Shop Homepage - Start Bootstrap Template</title>
                {/* Favicon*/}
                <link rel="icon" type="image/x-icon" href="../startbootstrap-shop-homepage-gh-pages/assets/favicon.ico" />
                {/* Bootstrap icons*/}
                <link href="https://cdn.jsdelivr.net/npm/bootstrap-icons@1.5.0/font/bootstrap-icons.css" rel="stylesheet" />
                {/* Core theme CSS (includes Bootstrap)*/}
                <link href="../startbootstrap-shop-homepage-gh-pages/css/styles.css" rel="stylesheet" />
                {/* Navigation*/}
                <nav className="navbar navbar-expand-lg navbar-light bg-light">
                    <div className="container px-4 px-lg-5">
                        <a className="navbar-brand" href="#!">Start Bootstrap</a>
                        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation"><span className="navbar-toggler-icon" /></button>
                        <div className="collapse navbar-collapse" id="navbarSupportedContent">
                            <ul className="navbar-nav me-auto mb-2 mb-lg-0 ms-lg-4">
                                <li className="nav-item"><a className="nav-link active" aria-current="page" href="#!">Home</a></li>
                                <li className="nav-item"><a className="nav-link active" href="#!">Discover</a></li>
                                <li className="nav-item"><a className="nav-link active" href="#!">Create A Campaign</a></li>
                            </ul>
                        </div>
                    </div>
                </nav>
                {/* Header*/}
                <header className="bg-dark py-5">
                    <div className="container px-4 px-lg-5 my-5">
                        <div className="text-center text-white">
                            <h1 className="display-4 fw-bolder">Shop in style</h1>
                            <p className="lead fw-normal text-white-50 mb-0">With this shop hompeage template</p>
                        </div>
                    </div>
                </header>
                {/* Section*/}
                <section className="py-5">
                    <div className="container px-4 px-lg-5 mt-5">
                        <div className="row gx-4 gx-lg-5 row-cols-2 row-cols-md-3 row-cols-xl-4 justify-content-center">
                            <div className="col mb-5">
                                <div className="card h-100">
                                    {/* Product image*/}
                                    <img className="card-img-top" src="https://dummyimage.com/450x300/dee2e6/6c757d.jpg" alt="..." />
                                    {/* Product details*/}
                                    <div className="card-body p-4">
                                        <div className="text-center">
                                            {/* Product name*/}
                                            <h5 className="fw-bolder">Fancy Product</h5>
                                            {/* Product price*/}
                                            $40.00 - $80.00
                                        </div>
